-- *************************************************************************
-- * Script que crea y carga datos en las tablas metrobus, subtes y trenes *
-- *************************************************************************

-- ********************
-- ** Tabla metrobus **
-- ********************


CREATE TABLE public.metrobus
(
    id integer,
    geom geometry,
    nombre character varying(80),
    lineas_al_norte character varying(100),
    lineas_al_sur character varying(100),
    metrobus character varying(50),
	PRIMARY KEY (id)
)
WITH (
    OIDS = FALSE
)
TABLESPACE pg_default;

ALTER TABLE public.metrobus
    OWNER to postgres;
	




-- ******************
-- ** Tabla subtes **
-- ******************


CREATE TABLE public.subtes
(
    id integer,
    geom geometry,
	linea character varying(2),
    estacion character varying(250),
	PRIMARY KEY (id)
)
WITH (
    OIDS = FALSE
)
TABLESPACE pg_default;

ALTER TABLE public.subtes
    OWNER to postgres;
	



-- ******************
-- ** Tabla trenes **
-- ******************


CREATE TABLE public.trenes
(
    id integer,
    geom geometry,
	linea character varying(250),
    ramal character varying(250),
	estacion character varying(250),
	barrio character varying(250),
	comuna character varying(250),
	PRIMARY KEY (id)
)
WITH (
    OIDS = FALSE
)
TABLESPACE pg_default;

ALTER TABLE public.trenes
    OWNER to postgres;
	
	
